import React, { useEffect, useState } from "react";
import { css } from '@emotion/css'

import {
    Calendar,
    List,
    Type,
} from "react-feather";

import Modal from "../Modal/Modal";
const { id, title, description, tag, duedate, assignee } = props.card;


function CardInfo(props) {
    const [values, setValues] = useState({
        ...props.card,
    });

    const updateTitle = (value) => {
        setValues({ ...values, title: value });
    };

    const updateDesc = (value) => {
        setValues({ ...values, desc: value });
    };


    useEffect(() => {
        if (props.updateCard) props.updateCard(props.boardId, values.id, values);
    }, [values]);

    return (
        <Modal onClose={props.onClose}>
            <div className={css` padding: 30px; display: flex; flex-direction: column; gap: 30px; min-width: 550px; width: fit-content;  max-width: 650px; height: fit-content;`}>
                <div className={css` width: 100%;  display: flex; flex-direction: column; gap: 10px;`}>
                    <div className={css`  display: flex; gap: 10px; align-items: center;`}>
                    <p className={css`display: flex;gap: 10px; flex-wrap: wrap;`}>Title:</p>
                        <input  defaultValue = {title} className={css`width: 100%; padding: 12px; border: 1px solid #ccc; border-radius: 4px; resize: vertical;`} type="text" />
                    </div>

                </div>

                <div className={css` width: 100%;  display: flex; flex-direction: column; gap: 10px;`}>
                    <div className={css`  display: flex; gap: 10px; align-items: center;`}>
                    <p className={css`display: flex;gap: 10px; flex-wrap: wrap;`}>Description:</p>
                        <input className={css`width: 100%; padding: 12px; border: 1px solid #ccc; border-radius: 4px; resize: vertical;`} type="text" />
                    </div>
                </div>

                <div className={css` width: 100%;  display: flex; flex-direction: column; gap: 10px;`}>
                    <div className={css`  display: flex; gap: 10px; align-items: center;`}>
                        <p>Tag:</p> 
                         <select className={css`width: 100%; padding: 16px 20px; border: none;  border-radius: 4px; background-color: #f1f1f1;`}>
                            <option value="au">SEO</option>
                            <option value="ca">Long Form</option>
                            <option value="usa">Blog post</option>
                        </select> 
                    </div>
                </div>

                <div className={css` width: 100%;  display: flex; flex-direction: column; gap: 10px;`}>
                    <div className={css`  display: flex; gap: 10px; align-items: center;`}>
                        <p className={css`display: flex;gap: 10px; flex-wrap: wrap;`}>Assignee:</p>
                        <input className={css`width: 100%; padding: 12px; border: 1px solid #ccc; border-radius: 4px; resize: vertical;`} type="text" />
                    </div>
                </div>

                <div className={css` width: 100%;  display: flex; flex-direction: column; gap: 10px;`}>
                    <div className={css`  display: flex; gap: 10px; align-items: center;`}>
                        <p>Due Date:</p>
                        <input className={css`width: fit-content; border: 2px solid #ddd; border-radius: 5px; outline: none; font-size: 1rem; padding: 10px;`} type="date" />
                    </div>
                </div>
            </div>
        </Modal>
    );
}

export default CardInfo;
