import logo from './logo.svg';
import './App.css';
import { css } from '@emotion/css'

//Components
import Header from './components/ui/header'
import Board from './components/ui/board'
import { useEffect, useState } from 'react';



function App() {
  const [ setMain] = useState([]);

  const main = {
    boards: [
        {
            id: 1,
            key:  "To Do",
            cards: []
        },
        {
            id: 2,
            key:  "In process",
            cards: [ ]
        },
        {
            id: 3,
            key:  "Done",
            cards: []
        }
    ]

}

const [targetCard, setTargetCard] = useState({
  bid: "",
  cid: "",
});

const [boards, setBoards] = useState(
  JSON.parse(localStorage.getItem("main")) || []
);

useEffect(() => {
  const boards  = JSON.parse(localStorage.getItem('main'))
  if(boards == null){
    localStorage.setItem('main', JSON.stringify(main));
  }
})
const addCardHandler = (id, title) => {
  const index = boards.findIndex((item) => item.id === id);
  if (index < 0) return;

  const tempBoards = [...boards];
  tempBoards[index].cards.push({
    id: Date.now() + Math.random() * 2,
    title,
    labels: [],
    date: "",
    tasks: [],
  });
  setBoards(tempBoards);
};

const removeCard = (bid, cid) => {
  const index = boards.findIndex((item) => item.id === bid);
  if (index < 0) return;

  const tempBoards = [...boards];
  const cards = tempBoards[index].cards;

  const cardIndex = cards.findIndex((item) => item.id === cid);
  if (cardIndex < 0) return;

  cards.splice(cardIndex, 1);
  setBoards(tempBoards);
};

const dragEnded = (bid, cid) => {
  let s_boardIndex, s_cardIndex, t_boardIndex, t_cardIndex;
  s_boardIndex = boards.findIndex((item) => item.id === bid);
  if (s_boardIndex < 0) return;

  s_cardIndex = boards[s_boardIndex]?.cards?.findIndex(
    (item) => item.id === cid
  );
  if (s_cardIndex < 0) return;

  t_boardIndex = boards.findIndex((item) => item.id === targetCard.bid);
  if (t_boardIndex < 0) return;

  t_cardIndex = boards[t_boardIndex]?.cards?.findIndex(
    (item) => item.id === targetCard.cid
  );
  if (t_cardIndex < 0) return;

  const tempBoards = [...boards];
  const sourceCard = tempBoards[s_boardIndex].cards[s_cardIndex];
  tempBoards[s_boardIndex].cards.splice(s_cardIndex, 1);
  tempBoards[t_boardIndex].cards.splice(t_cardIndex, 0, sourceCard);
  setBoards(tempBoards);

  setTargetCard({
    bid: "",
    cid: "",
  });
};

const dragEntered = (bid, cid) => {
  if (targetCard.cid === cid) return;
  setTargetCard({
    bid,
    cid,
  });
};

const updateCard = (bid, cid, card) => {
  const index = boards.findIndex((item) => item.id === bid);
  if (index < 0) return;

  const tempBoards = [...boards];
  const cards = tempBoards[index].cards;

  const cardIndex = cards.findIndex((item) => item.id === cid);
  if (cardIndex < 0) return;

  tempBoards[index].cards[cardIndex] = card;

  setBoards(tempBoards);
};

  return (


    <div className={css`height: 100vh; width: 100%; display: flex; flex-direction: column; border-radius: 4px; gap:20px;`}>
      <Header />
      <div className={css`flex:1; width:100%; overflow-x:auto; padding: 20px;`} >
        <div className={css`min-width: 100%; display: flex; gap:30px;`} >
          { main.boards.map(board =>
            <Board  
             key={board.id}
            board={board}
            addCard={addCardHandler}
            removeCard={removeCard}
            dragEnded={dragEnded}
            dragEntered={dragEntered}
            updateCard={updateCard} />
          )}

        </div>

      </div>

    </div>
  );
}

export default App;
